        //     fetchdata()
        //   }}}
      />
      <h1>{location}</h1>
      <button onClick={()=>{fetchdata()}}>search weather</button>
      <h1>Location:{text.name}</h1>
      {text.name ? <h1>{text.main.temp}Â°F</h1>:null}
      {text.weather?<h3>{text.weather[0].main}</h3>:null}
    </div>
    )
}
